// LOGIN LOGIC GOES HERE.
// Notes:
//   - Rename Login.js based on naming conventions after it is decided on (i.e. capitalizations for styles, main, and js files)
//   - Don't put everything in one file. Seperate into:
//       - Styles
//       - Logic (Main .js file)
//       - APIs (Accessory .js files)
import React from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from 'react-native';

const LoginScreen = () => {
  return (
    <View style={styles.container}>
      {/* Logo */}
      <Image source={Main} style={styles.logo} />

      {/* Login Form */}
      <View style={styles.formContainer}>
        <TextInput placeholder="Email or Username" style={styles.input} />
        <TextInput placeholder="Password" style={styles.input} secureTextEntry />

        {/* Sign In Button */}
        <TouchableOpacity style={styles.signInButton}>
          <Text style={styles.signInText}>Sign In</Text>
        </TouchableOpacity>

        {/* Divider */}
        <View style={styles.dividerContainer}>
          <View style={styles.line} />
          <Text style={styles.orText}>OR</Text>
          <View style={styles.line} />
        </View>

        {/* Social Login Buttons */}
        <TouchableOpacity style={styles.socialButton}>
          <Image source={{ uri: 'https://link-to-google-icon.png' }} style={styles.socialIcon} />
          <Text style={styles.socialText}>Sign in with Google</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.socialButton}>
          <Image source={{ uri: 'https://link-to-facebook-icon.png' }} style={styles.socialIcon} />
          <Text style={styles.socialText}>Sign in with Facebook</Text>
        </TouchableOpacity>

        {/* Sign Up Link */}
        <Text style={styles.signupText}>
          Need an account? <Text style={styles.signupLink}>Create your account</Text>
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#FF957C',
  },
  logo: {
    width: 100,
    height: 100,
    marginBottom: 20,
  },
  formContainer: {
    width: '80%',
    padding: 20,
    backgroundColor: '#fff',
    borderRadius: 10,
    alignItems: 'center',
  },
  input: {
    width: '100%',
    height: 40,
    borderBottomWidth: 1,
    borderBottomColor: '#B5B5B5',
    marginBottom: 15,
  },
  signInButton: {
    width: '100%',
    height: 45,
    backgroundColor: '#1CD05D',
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 10,
  },
  signInText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  dividerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 20,
  },
  line: {
    flex: 1,
    height: 1,
    backgroundColor: '#B5B5B5',
  },
  orText: {
    marginHorizontal: 10,
    color: '#B5B5B5',
  },
  socialButton: {
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    height: 45,
    borderWidth: 1,
    borderColor: '#B5B5B5',
    borderRadius: 10,
    marginVertical: 5,
    paddingHorizontal: 10,
  },
  socialIcon: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  socialText: {
    color: '#000',
    fontSize: 16,
  },
  signupText: {
    marginTop: 20,
    color: '#B5B5B5',
  },
  signupLink: {
    color: '#1CD05D',
    fontWeight: 'bold',
  },
});

export default LoginScreen;
