import React, { useState } from 'react';
import {
  SafeAreaView,
  ScrollView,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
} from 'react-native';
import styles from '../styles/LoginScreenStyles';

const LoginScreen = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Login logic goes here
    console.log("Email:", email);
    console.log("Password:", password);
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        {/* Top Row for Status Bar Icons */}
        <View style={styles.statusBar}>
          <Text style={styles.statusText}>9:41</Text>
          <Image source={require('../assets/statusIcon.png')} style={styles.icon} />
          <Image source={require('../assets/statusIcon.png')} style={styles.icon} />
          <Image source={require('../assets/statusIcon.png')} style={styles.iconLarge} />
        </View>

        {/* Main Login Content */}
        <View style={styles.loginContainer}>
          {/* Logo */}
          <Image source={require('../assets/logo.png')} style={styles.logo} />

          {/* Email Input */}
          <Text style={styles.label}>Email or Username</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter Email or Username"
            value={email}
            onChangeText={setEmail}
          />

          {/* Password Input */}
          <Text style={styles.label}>Password</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter Password"
            secureTextEntry
            value={password}
            onChangeText={setPassword}
          />

          {/* Sign In Button */}
          <TouchableOpacity style={styles.signInButton} onPress={handleLogin}>
            <Text style={styles.signInText}>Sign In</Text>
          </TouchableOpacity>

          {/* OR Divider */}
          <View style={styles.dividerContainer}>
            <View style={styles.dividerLine} />
            <Text style={styles.dividerText}>OR</Text>
            <View style={styles.dividerLine} />
          </View>

          {/* Social Login Buttons */}
          <TouchableOpacity style={styles.socialButton}>
            <Image source={require('../assets/googleIcon.png')} style={styles.socialIcon} />
            <Text style={styles.socialText}>Sign in with Google</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.socialButton}>
            <Image source={require('../assets/facebookIcon.png')} style={styles.socialIcon} />
            <Text style={styles.socialText}>Sign in with Facebook</Text>
          </TouchableOpacity>

          {/* Sign Up Link */}
          <Text style={styles.signUpText}>
            Need an account? <Text style={styles.signUpLink}>Create your account</Text>
          </Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

export default LoginScreen;
